INSERT INTO `ed_block_item_i18n` VALUES ('109', 'en_us', '1', 'Leading Advertising Service Team', '', '41', 'With the most excellent specialists in the digital marketing, Eptonic is committed to providing advertisers with the most professional and efficient service', 'upload/block/1473128969.png', '');
INSERT INTO `ed_block_item_i18n` VALUES ('109', 'zh_cn', '2', '国际顶尖团队', '', '41', '国际化顶尖投放团队<br>\n汇聚投放规划、效果优化等数字营销领域专家<br>\n致力于社交媒体广告投放<br>\n为广告主提供专业、高效、极致的推广服务<br>', 'upload/block/1473128969.png', '');
INSERT INTO `ed_block_item_i18n` VALUES ('110', 'en_us', '1', 'Customized Creative Service', '', '41', 'The customized advertising material will be created to help attract target audience based on the idea of  localization and deep understanding of social platform, like Facebook', 'upload/block/1473129043.png', '');
INSERT INTO `ed_block_item_i18n` VALUES ('110', 'zh_cn', '2', '定制创意设计', '', '41', '深度理解Facebook等社交平台广告独特表现手法和应用方式<br>\n由专业团队针对不同市场创作「本地化」的广告素材<br>\n高效吸引目标用户<br>', 'upload/block/1473129043.png', '');
INSERT INTO `ed_block_item_i18n` VALUES ('111', 'en_us', '1', 'Professional Advertising Campaign', '', '41', 'Eptonic offers advertisers professional and customized strategies of advertising campaign and traffic monitoring based on different product characteristics and market demands, helping enterprises to promote overseas', 'upload/block/1473130724.png', '');
INSERT INTO `ed_block_item_i18n` VALUES ('111', 'zh_cn', '2', '专业投放策略', '', '41', '根据产品特性和不同地区的市场需求<br>\n为广告主定制<br>专业、全面的投放策略以及流量监控方案<br>\n助力企业成功部署全球市场<br>', 'upload/block/1473130724.png', '');
INSERT INTO `ed_block_item_i18n` VALUES ('112', 'en_us', '1', 'Precision Marketing', '', '41', 'Create multi-dimensional precision marketing strategies according to real user information, operating behavior, and product orientation', 'upload/block/1473130827.png', '');
INSERT INTO `ed_block_item_i18n` VALUES ('112', 'zh_cn', '2', '精准营销', '', '41', '基于真实的用户信息、行为轨迹、操作习惯等数据<br>\n根据产品定位<br>\n通过多维度定向<br>\n对潜在的目标用户实现精准的广告投放<br>', 'upload/block/1473130827.png', '');
INSERT INTO `ed_block_item_i18n` VALUES ('113', 'en_us', '1', 'Full Performance Optimization ', '', '41', 'Have real-time tracking of advertising performance and  then full optimization to improve conversion rate and campaign performance', 'upload/block/1473130892.png', '');
INSERT INTO `ed_block_item_i18n` VALUES ('113', 'zh_cn', '2', '效果全程优化', '', '41', '投放过程中实时追踪投放效果<br>\n根据追踪效果全程优化<br>\n以精准的诊断和执行提高广告转化率<br>\n保证广告维持超高表现力<br>', 'upload/block/1473130892.png', '');
INSERT INTO `ed_block_item_i18n` VALUES ('114', 'en_us', '1', 'Search Marketing ', '', '42', 'Keep Cooperating with world-class search engines, like Google,Yahoo&Bing, and Yandex, build full coverage of search marketing network and reach active users in 200+countries', 'upload/block/uid_21_1474250899.png', '');
INSERT INTO `ed_block_item_i18n` VALUES ('114', 'zh_cn', '2', '全覆盖的搜索推广平台', '', '42', '通过与全球最大的搜索引擎Google<br>\n全球第二大搜索引擎Yahoo&Bing<br>\n俄语区最大的搜索引擎Yandex深度合作<br>\n构建起全覆盖的搜索营销网络<br>\n触达全球超过200个国家的优质活跃用户', 'upload/block/uid_21_1474250899.png', '');
INSERT INTO `ed_block_item_i18n` VALUES ('115', 'en_us', '1', 'Professional Team', '', '42', 'Eptonic has the most trusted team to explore enterprises’ promotion demand. Based on the keyword bidding technology and traffic resources, Eptonic helps to promote the advertising campaign in many aspects, improve the interaction and expand the global customers', 'upload/block/1473140403.png', '');
