INSERT INTO `ed_block_item_i18n` VALUES ('116', 'zh_cn', '2', '主动式精准定向', '', '42', '专业分析师深度解析<br>\n通过关键词竞价<br>\n将推广信息即时呈现在目标用户眼前<br>\n精准锁定目标客群<br>\n控制每一滴流量的去向<br>\n大幅度提升广告转化效果', 'upload/block/1473140613.png', '');
