INSERT INTO `ed_block_item_i18n` VALUES ('118', 'en_us', '1', 'Intelligent Optimization', '', '42', 'Eptonic provides automatic advertising service and intelligent optimization based on its self-developed automatic advertising optimization platform and system algorithm and helps advertisers achieve brand success', 'upload/block/1473140817.png', '');
